using System;\nusing System.Runtime.InteropServices;\nusing System.Windows.Forms;\n\npublic partial class MainForm : Form\n{\n    [StructLayout(LayoutKind.Sequential)]\n    public struct MARGINS\n    {\n        public int cxLeftWidth;      // width of left border that extends into client area\n        public int cxRightWidth;     // width of right border that extends into client area\n        public int cyTopHeight;      // height of top border that extends into client area\n        public int cyBottomHeight;   // height of bottom border that extends into client area\n    }\n\n    [DllImport("dwmapi.dll", CharSet = CharSet.Auto)]\n    public static extern int DwmExtendFrameIntoClientArea(IntPtr hWnd, ref MARGINS pMargins);\n\n    protected override void OnLoad(EventArgs e)\n    {\n        base.OnLoad(e);\n        if (Environment.OSVersion.Version >= new Version(6, 0))\n        {\n            MARGINS margins = new MARGINS() { cxLeftWidth = -1 };\n            DwmExtendFrameIntoClientArea(this.Handle, ref margins);\n        }\n    }\n}