using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Text;\n\nclass AnagramGrouper\n{\n    static void Main()\n    {\n        string line = Console.ReadLine();\n        string[] words = line.Split(' ');\n        Dictionary<string, StringBuilder> table = new Dictionary<string, StringBuilder>();\n\n        foreach (var word in words)\n        {\n            string key = GetAnagramKey(word);\n            if (!table.ContainsKey(key))\n            {\n                table[key] = new StringBuilder(word);\n            }\n            else\n            {\n                table[key].Append(" " + word);\n            }\n        }\n\n        foreach (var anagrams in table.Values)\n        {\n            Console.WriteLine(anagrams.ToString());\n        }\n    }\n\n    static string GetAnagramKey(string word)\n    {\n        char[] wordArray = word.ToCharArray();\n        Array.Sort(wordArray);\n        return new string(wordArray);\n    }\n}