using System;\nusing System.Data.SQLite;\nusing System.Windows;\n\nnamespace ContactManager\n{\n    public partial class MainWindow : Window\n    {\n        private string connectionString = "Data Source=yourdatabase.db;Version=3;";\n        \n        public MainWindow()\n        {\n            InitializeComponent();\n            LoadContacts();\n        }\n\n        private void LoadContacts()\n        {\n            using (SQLiteConnection connection = new SQLiteConnection(connectionString))\n            {\n                connection.Open();\n                string query = "SELECT * FROM Contacts";\n                using (SQLiteCommand command = new SQLiteCommand(query, connection))\n                using (SQLiteDataReader reader = command.ExecuteReader())\n                {\n                    // Assuming your DataGrid is called 'ContactsDataGrid'\n                    ContactsDataGrid.ItemsSource = System.Data.Linq.SqlClient.SqlMetal.GetQuery(reader);\n                }\n            }\n        }\n\n        // Method to add or update data - you need to implement SQL commands for this\n    }\n}