public static class SessionHelper\n{\n    public static void SetSessionValue(string key, object value)\n    {\n        var company = GetCurrentCompany(); // Implement this method to get the current company identifier from request context or URL\n        HttpContext.Current.Session[$"{company}_{key}"] = value;\n    }\n\n    public static object GetSessionValue(string key)\n    {\n        var company = GetCurrentCompany();\n        return HttpContext.Current.Session[$"{company}_{key}"];\n    }\n\n    private static string GetCurrentCompany()\n    {\n        // Implement this method to retrieve the company identifier\n        // For example, by parsing the query string or request path\n        var context = HttpContext.Current;\n        if (context.Request.QueryString["showProductsFrom"] != null)\n        {\n            return context.Request.QueryString["showProductsFrom"];\n        }\n        return "default"; // Fallback value\n    }\n}