public class FooData {\n    public double Price { get; set; }\n    public int Quantity { get; set; }\n}\n\npublic class FooBusinessObject {\n    public double Price { get; set; }\n    public int Quantity { get; set; }\n\n    public static FooBusinessObject ConvertFromFooData(FooData fooData) => new FooBusinessObject {\n        Price = fooData.Price,\n        Quantity = fooData.Quantity\n    };\n\n    public double GetTotal() => Price * Quantity;\n}