using System;\nusing System.Data;\n\npublic partial class _Default : System.Web.UI.Page \n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            // Fetch data from the SqlDataSource\n            DataView dv = (DataView)MyDataSource.Select(DataSourceSelectArguments.Empty);\n            if (dv != null && dv.Count > 0)\n            {\n                string title = dv[0]["TitleField"].ToString();\n                PageTitle.Text = title;\n            }\n        }\n    }\n}