NHibernate.Cfg.Configuration config = new NHibernate.Cfg.Configuration();\nconfig.Configure(); // Assumes you have hibernate.cfg.xml\n\nvar connectionString = "Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\\path\\to\\your\\database.mdb";\n\n// Define a database integration instance for MS Access\nvar msAccessIntegration = new NHibernate.Dialect.MsSqlCeDialect();\nconfig.SetProperty(NHibernate.Cfg.Environment.Dialect, typeof(MsAccessDialect).AssemblyQualifiedName);\nconfig.SetProperty(NHibernate.Cfg.Environment.ConnectionProvider, "NHibernate.Connection.DriverConnectionProvider");\nconfig.SetProperty(NHibernate.Cfg.Environment.ConnectionString, connectionString);\nconfig.SetProperty(NHibernate.Cfg.Environment.DriverClassName, "NHibernate.Driver.OleDbDriver");\n\nISessionFactory sessionFactory = config.BuildSessionFactory();\nISession session = sessionFactory.OpenSession();\n\n// Use the session to interact with the database