public class BackgroundTaskController : Controller\n{\n    public ActionResult StartLongRunningTask()\n    {\n        Task.Run(() => LongRunningMethod()); // Fire-and-forget approach\n\n        return View("Submitted");\n    }\n\n    private async void LongRunningMethod()\n    {\n        try\n        {\n            // Simulate long-running external system call\n            await System.Threading.Tasks.Task.Delay(5 * 60 * 1000); // 5 minutes delay for example\n\n            // Perform work here...\n        }\n        catch (Exception ex)\n        {\n            // Log exception\n        }\n    }\n}