// Create an instance of Table1Collection\nTable1Collection table1col = new Table1Collection();\n\n// Define the relation with the necessary conditions for the LEFT OUTER JOIN\nRelationCollection relationships = new RelationCollection();\nEntityField2 fieldFromTable2 = new EntityField2(typeof(Table2Fields), "Col5");\nIPredicateExpression joinCondition = new PredicateExpression(\n    new FieldCompareValuePredicate(fieldFromTable2, ComparisonOperator.Equal, "test")\n);\nRelationshipWithPredicates relationshipWithPredicates = new RelationshipWithPredicates(\n    Table1Entity.Relations.Table1HasLookupTable2sEntityUsingId,\n    joinCondition\n);\n\n// Add the relationship with conditions to the relation collection\nrelationships.Add(relationshipWithPredicates, JoinHint.Left);\n\n// Fetch the data using GetMulti method with the defined relations\ntable1col.GetMulti(relationships);\n\n// table1col now contains all records from Table1 with matching records from Table2 or NULL if no match is found