using FileHelpers;\n\n[DelimitedRecord(' ')]\npublic class LogEntry\n{\n    public string IP;\n    [FieldQuote('"')]\n    public string Request;\n    [FieldOptional]\n    public int StatusCode;\n    [FieldOptional]\n    public long BytesSent;\n    [FieldOptional, FieldQuoted(QuoteMode.OptionalForRead)]\n    public string Referer;\n    [FieldQuoted]\n    public string UserAgent;\n\n    // Custom converter for timestamp\n    [FieldConverter(ConverterKind.Date, "dd/MMM/yyyy:HH:mm:ss zzz", AllowNullValues = true)]\n    public DateTime Timestamp;\n\n    // Custom field parser will handle combined fields\n    [FieldHidden]\n    public string TimestampText;\n\n    [AfterRead]\n    private void AfterReadMethod()\n    {\n        var match = Regex.Match(Request, @"\[([^\]]+)\]");\n        if (match.Success)\n        {\n            TimestampText = match.Groups[1].Value;\n            Request = Request.Replace(TimestampText, "").Trim(new[] { '"', ' ', '[', ']' });\n        }\n    }\n}