using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string sqlCode = @"--comment 1 with;\nselect id from t_id;\n--comment 2 with ;\nselect name from t_id;\n/*comment 3 \nwith ;*/";\n\n        // Regex pattern to match semicolons within single-line comments\n        string singleLineCommentPattern = @"(--\s*.*?)(;)";\n        // Regex pattern to match semicolons within multi-line comments\n        string multiLineCommentPattern = @"(/\*.*?)(;)(?=.*?\*/)";\n\n        // Replace semicolon in single-line comments\n        sqlCode = Regex.Replace(sqlCode, singleLineCommentPattern, "$1~", RegexOptions.Singleline);\n        // Replace semicolon in multi-line comments\n        sqlCode = Regex.Replace(sqlCode, multiLineCommentPattern, "$1~", RegexOptions.Singleline | RegexOptions.Compiled);\n\n        Console.WriteLine(sqlCode);\n    }\n}