// POCO - No attributes, pure data model\npublic class User {\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\n// DTO - Used for data transfer between layers\npublic class UserDto {\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\n// AutoMapper profile to map between POCO and DTO\npublic class UserProfile : Profile {\n    public UserProfile() {\n        CreateMap<User, UserDto>();\n        CreateMap<UserDto, User>();\n    }\n}\n\n// Repository interface\npublic interface IUserRepository {\n    IEnumerable<User> GetAllUsers();\n    User GetUserById(int id);\n}\n\n// SQL Server specific implementation\npublic class SqlUserRepository : IUserRepository {\n    private readonly MyDbContext _context;\n\n    public SqlUserRepository(MyDbContext context) {\n        _context = context;\n    }\n\n    public IEnumerable<User> GetAllUsers() => _context.Users.ToList();\n\n    public User GetUserById(int id) => _context.Users.FirstOrDefault(u => u.Id == id);\n}\n\n// XML File specific implementation\npublic class XmlUserRepository : IUserRepository {\n    // Implementation details for XML storage\n}