using System;\nusing System.Collections.Generic;\nusing System.Linq.Expressions;\nusing System.Text;\nusing System.Web;\nusing System.Web.Mvc;\n\npublic static class HtmlExtensions\n{\n    public static MvcHtmlString ImageValidationSummary(this HtmlHelper htmlHelper)\n    {\n        if (htmlHelper.ViewData.ModelState.IsValid)\n            return MvcHtmlString.Empty;\n\n        var sb = new StringBuilder();\n        sb.AppendLine("<div style='color: red'>");\n        sb.AppendLine("<ul>");\n\n        foreach (var key in htmlHelper.ViewData.ModelState.Keys)\n        {\n            foreach (var error in htmlHelper.ViewData.ModelState[key].Errors)\n            {\n                if (!String.IsNullOrEmpty(error.ErrorMessage))\n                    sb.AppendFormat(\n                        "<li><img src='/path/to/error.png' alt='Error' /> {0}</li>", \n                        HttpUtility.HtmlEncode(error.ErrorMessage));\n            }\n        }\n\n        sb.AppendLine("</ul>");\n        sb.AppendLine("</div>");\n\n        return MvcHtmlString.Create(sb.ToString());\n    }\n}