using System;\nusing System.Data.SqlTypes;\nusing Microsoft.SqlServer.Server;\n\npublic class LargeTextHandler\n{\n    [SqlFunction]\n    public static SqlString ConcatLargeStrings(SqlString firstPart, SqlString secondPart)\n    {\n        if (firstPart.IsNull || secondPart.IsNull)\n            return SqlString.Null;\n\n        return new SqlString(firstPart.Value + secondPart.Value);\n    }\n}