using Telerik.WinControls.UI;\n\n// Initialize RadGridView and add data columns\nRadGridView radGridView = new RadGridView();\nradGridView.Columns.Add(new GridViewTextBoxColumn("Column1"));\nradGridView.Columns.Add(new GridViewTextBoxColumn("Column2"));\n\n// Set AutoFillMode to allow auto-fill through drag-and-drop\nradGridView.MasterTemplate.AutoFillMode = GridAutoFillMode.FillRows;\n\n// Add some sample data for demonstration\nfor (int i = 0; i < 5; i++)\n{\n    radGridView.Rows.Add(new object[] { "Row" + i, i * 10 });\n}\n\n// Add RadGridView to Form\nthis.Controls.Add(radGridView);