private void BuildView()\n{\n    Queue<Item> itemQueue = new Queue<Item>();\n    \n    // Get All Parent Nodes\n    DataView dv = new DataView(dt);\n    dv.RowFilter = "ParentID = 0";\n\n    // Add the parent nodes to the queue\n    foreach (DataRowView drv in dv)\n    {\n        Item item = new Item();\n        item.Text = Convert.ToString(drv["Text"]);\n        item.ID = Convert.ToInt32(drv["ID"]);\n        item.ParentID = Convert.ToInt32(drv["ParentID"]);\n        itemQueue.Enqueue(item);\n    }\n\n    // Go through the queue one node at a time\n    while (itemQueue.Count > 0)\n    {\n        Item currentItem = itemQueue.Dequeue();\n        Debug.WriteLine(currentItem.Text);\n\n        // Get children of current node\n        dv.RowFilter = string.Format("ParentID = {0}", currentItem.ID);\n        if (dv.Count > 0)\n        {\n            // Add child nodes to the queue\n            foreach (DataRowView drvChild in dv)\n            {\n                Item item = new Item();\n                item.Text = Convert.ToString(drvChild["Text"]);\n                item.ID = Convert.ToInt32(drvChild["ID"]);\n                item.ParentID = Convert.ToInt32(drvChild["ParentID"]);\n                itemQueue.Enqueue(item);\n            }\n        }\n    }\n}