using (var context = new NorthwindContext()) {\n    IEnumerable<Customer> customers = GetCustomers(); // Replace with your method to fetch customers\n    IEnumerable<Item> items = GetItems(); // Replace with your method to fetch items\n\n    foreach (var customer in customers) {\n        foreach (var item in items) {\n            var order = new Order {\n                CustomerId = customer.CustomerId,\n                ItemId = item.ItemId\n            };\n\n            context.Orders.Add(order);\n        }\n    }\n\n    context.SaveChanges();\n}