protected void DynamicTextBoxes_Init(object sender, EventArgs e) {\n    var properties = typeof(YourEntity).GetProperties();\n    foreach (var property in properties) {\n        var maxLengthAttribute = property.GetCustomAttributes(typeof(MaxLengthAttribute), false);\n        if (maxLengthAttribute.Length > 0) {\n            var textBox = new TextBox { ID = property.Name };\n            textBox.Attributes["maxlength"] = ((MaxLengthAttribute)maxLengthAttribute[0]).Value.ToString();\n            // Add the textBox to your form or panel\n            YourForm.Controls.Add(textBox);\n        }\n    }\n}