protected void LinqDataSource_Selecting(object sender, LinqDataSourceSelectEventArgs e)\n{\n    var context = new YourDataContext();\n    \n    // Enable logging (EF Core 6 and above)\n    using (var loggerFactory = LoggerFactory.Create(builder =>\n                        builder.AddConsole()))\n    {\n        context.Database.UseLoggerFactory(loggerFactory);\n        \n        var query = from item in context.YourTable\n                    where item.SomeProperty == someValue\n                    select item;\n        \n        e.Result = query.ToList(); // This will execute the query and log it to console (or other configured logger)\n    }\n}