// Before showing the form, add a new row and hold its reference\nDataRowView newRowView = (DataRowView)bindingSource.AddNew();\nDataTable dataTable = ((DataSet)bindingSource.DataSource).Tables[bindingSource.DataMember];\n\n// Assuming 'newRow' is based on your DataTable's schema\nDataRow newRow = newRowView.Row;\n\n// Set values for unbound columns\nnewRow["ForeignKeyColumn"] = someForeignKeyValue; // Example of setting a foreign key\n\n// Optionally, set any other column values directly here if needed\n\n// Move to the last position (new row)\nbindingSource.MoveLast();\n\n// Show your form or perform data entry operations at this point...\n\n// Save action:\nvoid SaveAction()\n{\n    // EndEdit on BindingSource commits the changes in the UI-bound columns\n    bindingSource.EndEdit();\n\n    // Insert using TableAdapter with the captured DataRow reference\n    tableAdapter.Insert(newRow);\n\n    // Other actions like updating the DataSet or refreshing bindings can be done here\n}