using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<string> Words = new List<string>();\n        // Assuming you have added ~170 elements to the list\n\n        var occurrencesDict = Words.GroupBy(word => word)\n                                   .ToDictionary(group => group.Key, group => group.Count());\n\n        foreach (var kvp in occurrencesDict)\n        {\n            Console.WriteLine("{0} ({1})", kvp.Key, kvp.Value);\n        }\n    }\n}