using System;\nusing Microsoft.SharePoint;\n\nnamespace SharePointPermissionAdder\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            using (SPSite site = new SPSite("http://yoursharepointsite"))\n            {\n                using (SPWeb web = site.OpenWeb())\n                {\n                    SPList list = web.Lists["Your List Name"];\n\n                    // Resolve principal by name. This could be a user or group\n                    SPRoleAssignment roleAssignment = web.EnsureUser("DOMAIN\\username"); // or "GroupName" for groups\n\n                    // Add the user/group to the role assignments if not already added\n                    if (!list.HasUniqueRoleAssignments)\n                        list.BreakRoleInheritance(true);\n\n                    if (roleAssignment != null && !list.RoleAssignments.Contains(roleAssignment))\n                    {\n                        list.RoleAssignments.Add(roleAssignment);\n                    }\n\n                    // Apply the 'Contribute' permission level to the user/group\n                    SPRoleDefinition roleDefinition = web.RoleDefinitions["Contribute"];\n                    roleAssignment.RoleDefinitionBindings.Add(roleDefinition);\n\n                    // Update the changes\n                    roleAssignment.Update();\n                    list.Update();\n\n                    Console.WriteLine("Permissions added successfully.");\n                }\n            }\n        }\n    }\n}