using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("kernel32.dll", SetLastError = true)]\n    static extern bool AllocConsole();\n\n    static void Main(string[] args)\n    {\n        // Check if the -debug argument is present\n        bool showConsole = Array.Exists(args, arg => string.Equals(arg, "-debug"));\n\n        if (showConsole)\n        {\n            // Allocate a console for this process\n            AllocConsole();\n            Console.WriteLine("Debug mode enabled. Press any key to exit...");\n        }\n\n        try\n        {\n            // Your main application logic here\n            Console.WriteLine("Application is running.");\n        }\n        catch (Exception ex)\n        {\n            if (showConsole)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n\n        if (showConsole)\n        {\n            // Wait for a key press before exiting to keep the console window open\n            Console.ReadKey();\n        }\n    }\n}