using System;\nusing System.Data.Odbc;\n\npublic void InsertXmlData(string xmlContent)\n{\n    using (OdbcConnection conn = new OdbcConnection("YourConnectionString"))\n    {\n        conn.Open();\n        string query = "EXEC YourStoredProcedure @xmlParam=?";\n        \n        using (OdbcCommand cmd = new OdbcCommand(query, conn))\n        {\n            // Create parameter and specify the type as OdbcType.NVarChar or OdbcType.VarChar\n            OdbcParameter xmlParameter = new OdbcParameter("@xmlParam", OdbcType.LongVarChar)\n            {\n                Value = xmlContent\n            };\n            \n            cmd.Parameters.Add(xmlParameter);\n            \n            cmd.ExecuteNonQuery();\n        }\n    }\n}