using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        for (int i = 0; i <= 100; i++)\n        {\n            Console.Write($"\rProgress: {i}%"); // \r moves the cursor to the start of the line\n            Thread.Sleep(50); // Simulate work with a short delay\n        }\n        Console.WriteLine(); // Move to the next line after completion\n    }\n}