using System;\nusing System.ComponentModel;\nusing System.Web.UI;\n\n[ExpressionPrefix("AppSettingsBool")]\npublic class AppSettingsBoolExpressionBuilder : ExpressionBuilder\n{\n    public override object EvaluateExpression(object target, BoundPropertyEntry entry, object parsedData)\n    {\n        var settingValue = bool.Parse(System.Configuration.ConfigurationManager.AppSettings[(string)entry.Expression] ?? "false");\n        return settingValue;\n    }\n\n    public static bool GetAppSettingsBool(string key)\n    {\n        return System.Convert.ToBoolean(System.Configuration.ConfigurationManager.AppSettings[key]);\n    }\n}\n\n// Usage in ASPX file\n<form id="form1" runat="server" style='<%# Eval("Background", "{0}=white:purple") %>'>