using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        if (IsCiscoVpnRunning())\n        {\n            Console.WriteLine("Cisco VPN Client is running.");\n        }\n        else\n        {\n            Console.WriteLine("Cisco VPN Client is not running.");\n        }\n    }\n\n    static bool IsCiscoVpnRunning()\n    {\n        foreach (Process process in Process.GetProcessesByName("cvpnd"))\n        {\n            if (!string.IsNullOrEmpty(process.ProcessName))\n            {\n                return true;\n            }\n        }\n        return false;\n    }\n}