using System;\nusing System.Windows.Forms;\n\npublic class LinkLabelExample : Form\n{\n    private LinkLabel linkLabel;\n\n    public LinkLabelExample()\n    {\n        linkLabel = new LinkLabel();\n        linkLabel.LinkClicked += OnLinkClicked;\n        linkLabel.Text = "Click here to visit example.com";\n        linkLabel.Links.Add(0, 18, "https://www.example.com");\n        this.Controls.Add(linkLabel);\n    }\n\n    private void OnLinkClicked(object sender, LinkLabelLinkClickedEventArgs e)\n    {\n        System.Diagnostics.Process.Start(new System.Diagnostics.ProcessStartInfo(e.Link.LinkData.ToString()) { UseShellExecute = true });\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new LinkLabelExample());\n    }\n}