protected override void OnPreRender(EventArgs e)\n{\n    base.OnPreRender(e);\n\n    // Reset GridView's PageIndex here based on application/business logic\n    // For example, if the user should be on first page after return from another page:\n    if (IsPostBack && !IsCallback)\n    {\n        gvResults.PageIndex = 0; // Set to appropriate index as per your logic\n        gvResults.DataSource = SearchResults;\n        gvResults.DataBind();\n    }\n}