using System;\nusing System.Globalization;\n\nclass Program\n{\n    static void Main()\n    {\n        // Get the current culture\n        var culture = CultureInfo.CurrentCulture;\n\n        // Determine the CSV separator\n        char csvSeparator = (culture.TextInfo.ListSeparator == ",") ? ';' : ',';\n\n        Console.WriteLine("CSV Separator for the current culture: " + csvSeparator);\n    }\n}