using System;\nusing System.Runtime.InteropServices;\n\n[Guid("YOUR_GUID_HERE"), InterfaceType(ComInterfaceType.InterfaceIsIUnknown)]\npublic interface ICallback {\n    void OnCallback();\n}\n\n// C# class that will use the callback\npublic class SomeClass {\n    private ICallback _callback;\n    \n    public void SetCallback(ICallback cb) {\n        _callback = cb;\n    }\n\n    public bool DoWork() {\n        // Perform some work, then call back\n        _callback.OnCallback();\n        return true;\n    }\n}