protected void ValidateUniqueName(object sender, EventArgs e)\n{\n    string newName = txtNewName.Text; // Assume txtNewName is your textbox ID\n    List<string> existingNames = GetExistingNames(); // Implement this method to fetch all names from other textboxes or database\n\n    if (existingNames.Contains(newName))\n    {\n        lblError.Visible = true;\n        lblError.Text = "This name is already in use. Please choose a different name.";\n        return; // Return early if the name is not unique\n    }\n\n    // Proceed with further processing if the name is unique\n}\n\nprivate List<string> GetExistingNames()\n{\n    var names = new List<string>();\n\n    // Fetch all other textboxes dynamically or from your database and add their Text to the list\n    // Example: Add values from ViewState, Session, Database etc.\n    foreach (var control in txtContainer.Controls) // Assuming txtContainer is a container holding your textboxes\n    {\n        if (control is TextBox textBox && !string.IsNullOrWhiteSpace(textBox.Text))\n        {\n            names.Add(textBox.Text);\n        }\n    }\n\n    return names;\n}