protected void btnUpload_Click(object sender, EventArgs e)\n{\n    if (fileUploadControl.HasFile)\n    {\n        byte[] imageBytes = new byte[fileUploadControl.PostedFile.ContentLength];\n        fileUploadControl.PostedFile.InputStream.Read(imageBytes, 0, imageBytes.Length);\n\n        using (SqlConnection conn = new SqlConnection("your_connection_string_here"))\n        {\n            string query = "UPDATE UserProfile SET ProfilePicture = @ProfilePicture WHERE UserID = @UserID";\n            using SqlCommand cmd = new SqlCommand(query, conn);\n            cmd.Parameters.AddWithValue("@ProfilePicture", imageBytes);\n            cmd.Parameters.AddWithValue("@UserID", GetCurrentUserID()); // Implement this to get the current user ID\n\n            conn.Open();\n            int result = cmd.ExecuteNonQuery();\n\n            if (result > 0)\n                lblMessage.Text = "Image uploaded successfully.";\n        }\n    }\n}