public void GetRecordsByTableName(string tableName)\n{\n    string sql = "SELECT * FROM " + SqlEscapeIdentifier(tableName);\n\n    // Assuming CommonRecord is a type that matches the structure of the tables\n    var records = dataContext.ExecuteQuery<CommonRecord>(sql);\n\n    ViewData["recordsByTableName"] = records.ToList();\n}\n\nprivate static string SqlEscapeIdentifier(string identifier)\n{\n    return "[" + identifier.Replace("]", "]]") + "]";\n}