using System;\nusing System.Threading.Tasks;\nusing SIPSorcery.SIP.App;\nusing SIPSorcery.Media;\n\nnamespace SIPDialerExample\n{\n    class Program\n    {\n        private static SIPTransport _sipTransport;\n        private static UserAgent _userAgent;\n\n        static async Task Main(string[] args)\n        {\n            // Initialize SIP transport and user agent.\n            var sipEndPoint = SIPEndPoint.Parse(SIPURI.Parse("sip:your-sip-domain.com").Host);\n            _sipTransport = new SIPTransport(sipEndPoint.GetIPEndPoint(), null, false);\n            \n            var userAgentDelegate = new UserAgent delegate\n            {\n                OnCallConnecting = OnCallConnecting,\n                OnRinging = OnRinging,\n                OnEarlyMedia = OnEarlyMedia,\n                OnCallAnswered = OnCallAnswered,\n            };\n\n            _userAgent = new UserAgent(userAgentDelegate, _sipTransport);\n\n            // Make a call.\n            Console.WriteLine("Dialing...");\n            await _userAgent.Dial(SIPURI.Parse("sip:destination-number@your-sip-domain.com"));\n\n            Console.ReadLine();\n        }\n\n        private static void OnCallConnecting(UserAgentClient uac)\n        {\n            Console.WriteLine($"Call connecting to {uac.TargetUri}");\n        }\n\n        private static SIPDialplanContext OnRinging(SIPUserAgent userAgent, SIPEndPoint remoteEndPoint, SIPRequest sipRequest)\n        {\n            Console.WriteLine("Remote party is ringing...");\n            return null; // Indicate that we will accept the call.\n        }\n\n        private static void OnEarlyMedia(UserAgentClient uac)\n        {\n            Console.WriteLine("Received early media.");\n        }\n\n        private static void OnCallAnswered(SIPUserAgent userAgent, SIPResponse sipResponse, SIPDialogue dialogue)\n        {\n            Console.WriteLine("Call answered!");\n            // Here you would manage call handling (e.g., playing/record audio).\n        }\n    }\n}