using System;\nusing System.Management;\n\npublic class ProcessHelper\n{\n    public static void Main()\n    {\n        DateTime startTime = DateTime.Now;\n        \n        // Instantiate your COM class here\n        MyApp.ApplicationClass anApp = new MyApp.ApplicationClass();\n        \n        TimeSpan creationThreshold = TimeSpan.FromSeconds(1); // Adjust as necessary\n\n        string query = $"SELECT ProcessId FROM Win32_Process WHERE CreationDate >= '{startTime.ToUniversalTime().ToString("yyyyMMddHHmmss.ffffff+UUU")}'";\n        \n        ManagementObjectSearcher searcher = new ManagementObjectSearcher(query);\n        \n        foreach (ManagementObject process in searcher.Get())\n        {\n            int pid = Convert.ToInt32(process["ProcessId"]);\n            Console.WriteLine($"Found PID: {pid}");\n            \n            // You can break after finding the first one if it's enough\n            break;\n        }\n    }\n}