// Correct endpoint URL for sending a single message\nstring apiurl = "https://api.zeepmobile.com/messaging/2008-07-14/send_message";\n\nUri reqUrl = new Uri(apiurl);\nTcpClient client = new TcpClient(reqUrl.Host, reqUrl.Port);\n\nNetworkStream netStream = client.GetStream();\nSslStream sslStream = new SslStream(netStream, false, ValidateServerCertificate);\nsslStream.AuthenticateAsClient(reqUrl.Host);\n\n// Corrected content length and request URI path\nstring parameters = "body=" + body;\nstring http_date = DateTime.UtcNow.ToString(PATTERN_RFC1123);\nbyte[] hashValue = hmacsha1.ComputeHash(Encoding.UTF8.GetBytes(API_KEY + ":" + http_date));\nstring b64Mac = Convert.ToBase64String(hashValue);\n\nStreamWriter s = new StreamWriter(sslStream);\ns.WriteLine(String.Format("POST {0} HTTP/1.1", reqUrl.AbsolutePath)); // Use correct path\ns.WriteLine(String.Format("Host: {0}", reqUrl.Host));\ns.WriteLine(String.Format("Authorization: Zeep {0}:{1}", API_KEY, b64Mac));\ns.WriteLine(String.Format("Date: {0}", http_date));\ns.WriteLine(String.Format("Content-Type: {0}", "application/x-www-form-urlencoded"));\ns.WriteLine(String.Format("Content-Length: {0}", Encoding.UTF8.GetByteCount(parameters))); // Corrected length\ns.WriteLine(); // Ensure an empty line between headers and body\ns.Write(parameters);\ns.Flush();\n\nStreamReader r = new StreamReader(sslStream);\nstring resp = r.ReadToEnd();\nConsole.WriteLine(resp);\nr.Close();