protected void LinqDataSource_Selecting(object sender, LinqDataSourceSelectEventArgs e)\n{\n    var context = new YourDataContext();\n    \n    // Enable logging of generated SQL (EF 6)\n    context.Database.Log = s => Debug.WriteLine(s);\n    \n    // Assuming you have a query defined like this:\n    var query = from item in context.YourTable\n                where item.SomeProperty == someValue\n                select item;\n    \n    e.Result = query;\n\n    // The generated SQL will be printed to the debug output.\n}\n\n// In your web.config, ensure you have the following for debugging output (if using Visual Studio):\n// <system.diagnostics>\n//     <trace autoflush="false" indentsize="4">\n//         <listeners>\n//             <add name="myListener"\n//               type="System.Diagnostics.TextWriterTraceListener"\n//               initializeData="c:\temp\DbTrace.log" />\n//             <remove name="Default" />\n//         </listeners>\n//     </trace>\n// </system.diagnostics>