using System;\nusing System.Data;\nusing System.Linq;\n\n// Assuming 'dataTable' is your DataTable that you have filled with data from MySQL.\nDateTime targetDate = myDateTime.Date; // Your DateTime object\n\n// Filter using LINQ\nvar filteredResult = dataTable.AsEnumerable()\n                              .Where(row => row.Field<DateTime>("myDateField").Date == targetDate)\n                              .CopyToDataTable();\n\n// Create a new BindingSource and set its DataSource to the filtered DataTable.\nBindingSource filteredBindingSource = new BindingSource();\nfilteredBindingSource.DataSource = filteredResult;\n\n// Now bind your control (e.g., DataGridView) to this BindingSource\nmyDataGridView.DataSource = filteredBindingSource;