using System;\nusing System.IO.MemoryMappedFiles;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Create or open a memory-mapped file.\n        using (MemoryMappedFile mmf = MemoryMappedFile.CreateOrOpen("MyMMF", 1024 * 1024))\n        {\n            // Create a view accessor with read/write access.\n            using (var accessor = mmf.CreateViewAccessor())\n            {\n                byte[] message = System.Text.Encoding.UTF8.GetBytes("Hello from MMF!");\n                \n                // Write the byte array to shared memory.\n                accessor.WriteArray(0, message, 0, message.Length);\n\n                Console.WriteLine("Message written to MMF. Press Enter to exit...");\n                Console.ReadLine();\n            }\n        }\n    }\n}