using System.Text;\n\n// Example of building a SQL query with dynamic conditions\nStringBuilder sqlBuilder = new StringBuilder("SELECT * FROM TableName WHERE 1=1");\n\nif (!string.IsNullOrEmpty(someCondition))\n{\n    sqlBuilder.AppendFormat(" AND ColumnName = '{0}'", someCondition);\n}\n\nif (anotherCondition)\n{\n    sqlBuilder.Append(" AND AnotherColumn > 10");\n}\n\nstring finalQuery = sqlBuilder.ToString();