protected void Page_Load(object sender, EventArgs e)\n{\n    int threadIdInCache = -1;\n    if (Int32.TryParse(HttpContext.Current.Cache["BackgroundThreadId"].ToString(), out threadIdInCache))\n    {\n        bool isAlive = IsThreadAlive(threadIdInCache);\n        if (isAlive)\n        {\n            ClientScript.RegisterStartupScript(GetType(), "timer", "setTimeout(function() { location.reload(); }, 5000);", true);\n        }\n        else\n        {\n            Response.Write("Work Completed!");\n        }\n    }\n}\n\nprivate bool IsThreadAlive(int threadId)\n{\n    foreach (ProcessThread pt in Process.GetCurrentProcess().Threads)\n    {\n        if (pt.Id == threadId) return true;\n    }\n    return false;\n}