using System;\nusing System.IO;\nusing iText.Kernel.Pdf;\nusing iText.Kernel.Pdf.Annot;\n\npublic class PdfAnnotationExtractor\n{\n    public static void ExtractAnnotations(string filePath)\n    {\n        using (PdfDocument pdfDoc = new PdfDocument(new PdfReader(filePath)))\n        {\n            int numberOfPages = pdfDoc.GetNumberOfPages();\n\n            for (int i = 1; i <= numberOfPages; i++)\n            {\n                var page = pdfDoc.GetPage(i);\n                var annotations = page.GetAnnotations();\n\n                foreach (PdfAnnotation annotation in annotations)\n                {\n                    Console.WriteLine($"Type: {annotation.GetType()}");\n                    // Extract more details based on the type of annotation\n                    if (annotation is PdfTextAnnotation textAnnot)\n                    {\n                        Console.WriteLine("Content: " + textAnnot.GetContentsAsString());\n                    }\n                }\n            }\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string path = "yourfile.pdf";\n        ExtractAnnotations(path);\n    }\n}