using (var dc = new System1ConfigDataContext())\n{\n    KeeperOfControls.DataContext = from segment in dc.tblSegments\n                                     where segment.site == iTemplateSite && segment.id == iTemplateSid\n                                     select segment;\n    foreach (var segment in KeeperOfControls.DataContext as IEnumerable<Segment>)\n    {\n        segment.Children = segment.GetChildren(dc).ToList(); // Force loading within context lifetime\n    }\n}