protected void Page_PreRender(object sender, EventArgs e)\n{\n    SwapTextBoxesWithLabels(this);\n}\n\nprivate void SwapTextBoxesWithLabels(Control parent)\n{\n    foreach (Control control in parent.Controls)\n    {\n        if (control is TextBox textBox && !textBox.Enabled)\n        {\n            Label label = new Label();\n            label.Text = textBox.Text;\n            label.ID = textBox.ID; // Optionally keep the same ID for easy reference\n            label.CssClass = textBox.CssClass; // Optional: keep CSS styling\n\n            parent.Controls.Remove(textBox);\n            parent.Controls.Add(label);\n        }\n        else if (control.HasControls())\n        {\n            SwapTextBoxesWithLabels(control); // Recursive call for nested controls\n        }\n    }\n}