using System;\n\npublic class CRCCalculator\n{\n    public static ushort CalculateCRCB(byte[] data)\n    {\n        ushort crc = 0xFFFF; // Initial value\n        ushort polynomial = 0x8408;\n\n        foreach (byte b in data)\n        {\n            crc ^= (ushort)(b << 8);\n            for (int i = 0; i < 8; i++)\n            {\n                if ((crc & 0x8000) != 0)\n                {\n                    crc = (ushort)((crc << 1) ^ polynomial);\n                }\n                else\n                {\n                    crc <<= 1;\n                }\n            }\n        }\n\n        return crc;\n    }\n\n    public static void Main()\n    {\n        byte[] data1 = { 0x00, 0x00, 0x00 };\n        byte[] data2 = { 0x0F, 0xAA, 0xFF };\n\n        Console.WriteLine("CRC_B for {0} is: {1}",\n            BitConverter.ToString(data1), CalculateCRCB(data1).ToString("X4"));\n        Console.WriteLine("CRC_B for {0} is: {1}",\n            BitConverter.ToString(data2), CalculateCRCB(data2).ToString("X4"));\n    }\n}