using (PdfReader reader = new PdfReader(inputPath)) {\n       using (FileStream fs = new FileStream(outputPath, FileMode.Create, FileAccess.Write, FileShare.None)) {\n           using (PdfStamper stamper = new PdfStamper(reader, fs)) {\n               for (int i = 1; i <= reader.NumberOfPages; i++) {\n                   PdfContentByte overContent = stamper.GetOverContent(i);\n                   // Example: Add text at specific coordinates\n                   float xInPoints = 300; // Position in points\n                   float yInPoints = 450; // Position in points\n                   overContent.BeginText();\n                   overContent.SetFontAndSize(BaseFont.CreateFont(BaseFont.HELVETICA, BaseFont.WINANSI, false), 12);\n                   overContent.ShowTextAligned(PdfContentByte.ALIGN_LEFT, "Sample Text", xInPoints, yInPoints, 0);\n                   overContent.EndText();\n               }\n           }\n       }\n   }