using System;\nusing System.IO;\nusing Tetra.dll;\n\npublic class OCREngine\n{\n    public string ConvertImageToText(string imageFilePath)\n    {\n        if (!File.Exists(imageFilePath))\n            throw new FileNotFoundException("The file does not exist.", imageFilePath);\n\n        var ocr = new Tesseract();\n        try\n        {\n            // Initialize the engine with path to tessdata directory and language\n            ocr.Init(@"./tessdata", "eng", OcrEngineMode.Default);\n            \n            using (var img = Pix.LoadFromFile(imageFilePath))\n            {\n                ocr.SetImage(img);\n\n                var page = ocr.Process();\n                var text = page.GetText();\n\n                return text;\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error during OCR: " + ex.Message);\n            throw;\n        }\n        finally\n        {\n            ocr.End();\n        }\n    }\n}