using PdfiumViewer;\nusing System.IO;\nusing System.Windows;\n\nnamespace WpfApp\n{\n    public partial class MainWindow : Window\n    {\n        private RenderedPdfDocument renderedPdf;\n        private int currentPage = 1;\n\n        public MainWindow()\n        {\n            InitializeComponent();\n            DisplayPdf("path/to/your/document.pdf");\n        }\n\n        private void DisplayPdf(string path)\n        {\n            using (var document = PdfDocument.Load(path))\n            {\n                renderedPdf?.Dispose();\n                renderedPdf = document.Render(currentPage, 200f / 100f);\n            }\n            imgViewer.Source = renderedPdf.AsBitmapSource();\n        }\n    }\n}