protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n  {\n      if (e.Row.RowType == DataControlRowType.DataRow)\n      {\n          int termId = Convert.ToInt32(DataBinder.Eval(e.Row.DataItem, "TermId"));\n          string term;\n          switch (termId)\n          {\n              case 1:\n                  term = "30 days";\n                  break;\n              case 2:\n                  term = "60 days";\n                  break;\n              default:\n                  term = termId.ToString();\n                  break;\n          }\n          e.Row.Cells[/*index of TermCell*/].Text = term;\n      }\n  }